extern crate odbc;
// Use this crate and set environmet variable RUST_LOG=odbc to see ODBC warnings
extern crate env_logger;
use odbc::*;
use odbc_safe::AutocommitOn;
use std::env;

fn main() {

    env_logger::init();

    let conn_str = env::var("DSN").unwrap();
    match connect(&conn_str) {
        Ok(()) => println!("Success"),
        Err(diag) => println!("Error: {}", diag),
    }
}

fn connect(conn_str: &str) -> std::result::Result<(), DiagnosticRecord> {

    let env = create_environment_v3().map_err(|e| e.unwrap())?;

    let conn = env.connect_with_connection_string(conn_str)?;
    execute_statement(&conn)
}

fn execute_statement<'env>(conn: &Connection<'env, AutocommitOn>) -> Result<()> {
    let stmt = Statement::with_parent(conn)?;

    match stmt.exec_direct("select * from m.t")? {
        Data(mut stmt) => {
            let cols = stmt.num_result_cols()?;
            println!("cols: {}", cols);
            while let Some(mut cursor) = stmt.fetch()? {
                for i in 1..(cols + 1) {
                    match cursor.get_data::<&str>(i as u16)? {
                        Some(val) => print!(" {}", val),
                        None => print!(" NULL"),
                    }
                }
                println!("");
            }
        }
        NoData(_) => println!("Query executed, no data returned"),
    }

    Ok(())
}

use libtaos::*;

fn taos_connect() -> Result<Taos, Error> {
    TaosCfgBuilder::default()
        .ip("localhost")
        .user("root")
        .pass("taosdata")
        // .db("log") // remove comment if you want to connect to database log by default.
        .port(6030u16)
        .build()
        .expect("TaosCfg builder error")
        .connect()
}

fn main() {
    #[allow(unused_variables)]
    let taos = taos_connect().unwrap();
    println!("Connected")
}

use bstr::BString;
use libtaos::*;

#[tokio::main]
async fn main() -> Result<(), Error> {
    let taos = TaosCfg::default().connect().expect("fail to connect");
    taos.create_database("power").await?;
    taos.use_database("power").await?;
    taos.exec("CREATE STABLE meters (ts TIMESTAMP, current FLOAT, voltage INT, phase FLOAT) TAGS (location BINARY(64), groupId INT)").await?;
    let mut stmt = taos.stmt("INSERT INTO ? USING meters TAGS(?, ?) VALUES(?, ?, ?, ?)")?;
    // bind table name and tags
    stmt.set_tbname_tags(
        "d1001",
        [
            Field::Binary(BString::from("California.SanFrancisco")),
            Field::Int(2),
        ],
    )?;
    // bind values.
    let values = vec![
        Field::Timestamp(Timestamp::new(1648432611249, TimestampPrecision::Milli)),
        Field::Float(10.3),
        Field::Int(219),
        Field::Float(0.31),
    ];
    stmt.bind(&values)?;
    // bind one more row
    let values2 = vec![
        Field::Timestamp(Timestamp::new(1648432611749, TimestampPrecision::Milli)),
        Field::Float(12.6),
        Field::Int(218),
        Field::Float(0.33),
    ];
    stmt.bind(&values2)?;
    // execute
    stmt.execute()?;
    Ok(())
}

