extern crate bindgen;

use std::env;
use std::path::PathBuf;

fn main() {
    // Tell cargo to tell rustc to link the system bzip2
    // shared library.
    println!("cargo:rustc-link-lib=taos");

    // Tell cargo to invalidate the built crate whenever the wrapper changes
    println!("cargo:rerun-if-changed=wrapper.h");

    // The bindgen::Builder is the main entry point
    // to bindgen, and lets you build up options for
    // the resulting bindings.
    let bindings = bindgen::Builder::default()
        // The input header we would like to generate
        // bindings for.
        .header("wrapper.h")
        // Tell cargo to invalidate the built crate whenever any of the
        // included header files changed.
        .parse_callbacks(Box::new(bindgen::CargoCallbacks))
        // Finish the builder and generate the bindings.
        .generate()
        // Unwrap the Result and panic on failure.
        .expect("Unable to generate bindings");

    // Write the bindings to the $OUT_DIR/bindings.rs file.
    let out_path = PathBuf::from(env::var("OUT_DIR").unwrap());
    bindings
        .write_to_file(out_path.join("bindings.rs"))
        .expect("Couldn't write bindings!");
}

use libtdengine::tdengine::{clean_up, Tdengine};
use libtdengine::utils;
use std::process;

fn main() {
    let tde = Tdengine::new("127.0.0.1:6030", "root", "taosdata", "log", 0).unwrap_or_else(|err| {
        eprintln!("Can't create Tdengine: {}", err);
        process::exit(1)
    });

    assert_eq!(tde.query("drop database demo").is_ok(), true);
    assert_eq!(tde.query("create database demo").is_ok(), true);
    assert_eq!(tde.query("use demo").is_ok(), true);
    assert_eq!(
        tde.query("create table m1 (ts timestamp, speed int)")
            .is_ok(),
        true
    );

    for i in 0..10 {
        assert_eq!(
            tde.query(format!("insert into m1 values (now+{}s, {})", i, i).as_str())
                .is_ok(),
            true
        );
    }
    let rows = tde.query("select * from m1").unwrap_or_else(|err| {
        eprintln!("select error: {}", err);
        process::exit(1);
    });
    for row in rows {
        println!("{}", utils::format_row(&row));
    }
    // drop manually before clean up
    drop(tde);
    // cleanup before program exit
    clean_up();
}

use libtdengine::subscriber::Subscriber;
use libtdengine::tdengine::{clean_up, Tdengine};
use std::process;

fn main() {
    let td = Tdengine::new("127.0.0.1", "root", "taosdata", "demo", 0).unwrap_or_else(|err| {
        eprintln!("Can't create Tdengine: {}", err);
        process::exit(1)
    });

    let mut subscriber =
        Subscriber::subscribe(td.taos(), 0, "topic_test", "select * from m1;", 1000)
            .unwrap_or_else(|err| {
                eprintln!("Can't create Subscriber: {}", err);
                process::exit(1)
            });

    loop {
        let rows = match subscriber.consume() {
            Ok(rows) => rows,
            Err(err) => {
                eprintln!("consume exit: {}", err);
                break;
            }
        };

        // example code
        for row in rows {
            println!("({}, {})", row[0].as_i64(), row[1].as_i32());
        }
        println!("====================");
    }
    drop(td);
    clean_up();
}

