        unsafe { &(*(::std::ptr::null::<TAOS_BIND>())).length as *const _ as usize },
        24usize,
        concat!(
            "Offset of field: ",
            stringify!(TAOS_BIND),
            "::",
            stringify!(length)
        )
    );
    assert_eq!(
        unsafe { &(*(::std::ptr::null::<TAOS_BIND>())).is_null as *const _ as usize },
        32usize,
        concat!(
            "Offset of field: ",
            stringify!(TAOS_BIND),
            "::",
            stringify!(is_null)
        )
    );
    assert_eq!(
        unsafe { &(*(::std::ptr::null::<TAOS_BIND>())).is_unsigned as *const _ as usize },
        40usize,
        concat!(
            "Offset of field: ",
            stringify!(TAOS_BIND),
            "::",
            stringify!(is_unsigned)
        )
    );
    assert_eq!(
        unsafe { &(*(::std::ptr::null::<TAOS_BIND>())).error as *const _ as usize },
        48usize,
        concat!(
            "Offset of field: ",
            stringify!(TAOS_BIND),
            "::",
            stringify!(error)
        )
    );
}
