use clap::Args;
use futures::Future;
use parquet::{basic::Compression, schema::types::Type};
use std::{
    fmt::Debug,
    fs::{self, File},
    io::Write,
    path::PathBuf,
    sync::Arc,
};
use taos::prelude::*;
use taos::TaosOptions;

use taosx::{Database, TaosOpts};
use thread_id;
use tokio::runtime::Builder;

use crate::commands::backup::{
    fetch::{fetch_database_info, fetch_table_list},
    schema::TaosParquetSchema,
};

use self::{fetch::fetch_stable_tag_buffer, serialize::Serialize};

mod fetch;
mod schema;
mod serialize;

#[derive(Debug, Args, Clone)]
/// Backup database or tables to specific files.
///
/// Basically, an alternative command to `taosdump`.
pub(crate) struct App {
    #[clap(short, long)]
    database: Option<String>,
    #[clap(short, long)]
    output: Option<String>,
    #[clap(short, long)]
    thread: Option<u32>,
}

fn allocate_task<Fut: 'static>(
    table_list: Vec<String>,
    schema: Arc<Type>,
    dir: PathBuf,
    path: &str,
    mut threads: u32,
    db: String,
    f: impl Fn(Arc<Type>, Vec<String>, PathBuf, String) -> Fut,
) where
    Fut: Future<Output = ()> + std::marker::Send,
{
    let mut own_path = dir;
    own_path.push(path);
    fs::create_dir(own_path.clone()).unwrap_or_else(|_| {
        fs::remove_dir_all(own_path.clone()).unwrap_or_else(|_| {
            fs::remove_dir(own_path.clone()).unwrap();
        });
        fs::create_dir(own_path.clone()).unwrap();
    });
    if threads > table_list.len() as _ {
        threads = table_list.len() as _;
    }
    let tables_per_thread = if table_list.len() as u32 % threads == 0 {
        table_list.len() as u32 / threads
    } else {
        table_list.len() as u32 / threads + 1
    };

    let mut handles = Vec::with_capacity(threads as _);
    let chunks: Vec<&[String]> = table_list.chunks(tables_per_thread as _).collect();
    threads = chunks.len() as _;

    log::info!(
        "{} threads each deal at most {} tables",
        threads,
        tables_per_thread
    );

    let runtime = Builder::new_multi_thread()
        .worker_threads(threads as usize)
        .enable_all()
        .build()
        .unwrap();

    for chunk in chunks {
        handles.push(runtime.spawn(f(
            schema.clone(),
            chunk.to_owned(),
            own_path.clone(),
            db.clone(),
        )));
    }
    for handle in handles {
        runtime.block_on(handle).unwrap();
    }
}

impl App {
    pub fn run_with_taos_opts(&self, _opts: &TaosOpts) {
        log::info!("prepare config options");

        let database = self.database.as_deref().unwrap_or("test");
        let db = String::from(database);
        let threads = self.thread.unwrap_or(1);
        let output = self.output.as_deref().unwrap_or("./");
        let path = PathBuf::from(output);

        log::info!("prepare parquet schema");

        let schema = TaosParquetSchema::default().build();

        log::info!("start backup database info");

        let database = fetch_database_info(db.clone());

        backup_database(database, path.clone());

        log::info!("finish backup database info");

        let (select_list, describe_list, stable_list) = fetch_table_list(db.clone());

        log::info!(
            "select list: {:?}; describe list {:?}; stable_list{:?}",
            select_list,
            describe_list,
            stable_list
        );

        log::info!("start backup table meta info");
        allocate_task(
            describe_list,
            schema.clone(),
            path.clone(),
            "table.info",
            threads,
            db.clone(),
            backup_table_schema,
        );
        log::info!("finish backup table meta info");
        log::info!("start backup table tags");
        allocate_task(
            stable_list,
            schema.clone(),
            path.clone(),
            "tags",
            threads,
            db.clone(),
            backup_stable_tags,
        );
        log::info!("finish backup table tags");
        log::info!("start backup data");
        allocate_task(select_list, schema, path, "chunk", threads, db, backup_data);
        log::info!("finish backup data");
    }
}

fn backup_database(database: Database, mut path: PathBuf) {
    path.push("db.info");
    let mut file = File::create(path.as_path()).unwrap();
    let j = serde_json::to_string_pretty(&database).unwrap();
    file.write_all(j.as_bytes()).unwrap();
}

async fn backup_table_schema(
    schema: Arc<Type>,
    table_list: Vec<String>,
    mut path: PathBuf,
    db: String,
) {
    let thread_id = thread_id::get();
    path.push(format!("{}.parquet", thread_id));
    let file = File::create(path.as_path()).unwrap();
    let taos = TaosOptions::new().database(db.clone()).build().unwrap();
    let mut serialize = Serialize::new(file, Compression::SNAPPY, schema);
    for tbname in table_list {
        let describe = taos.describe(&tbname).await.unwrap();
        serialize.serialze_table_meta(&tbname, describe);
    }
}

async fn backup_stable_tags(
    schema: Arc<Type>,
    table_list: Vec<String>,
    mut path: PathBuf,
    database: String,
) {
    let thread_id = thread_id::get();
    path.push(format!("{}.parquet", thread_id));
    let file = File::create(path.as_path()).unwrap();
    let mut serialize = Serialize::new(file, Compression::SNAPPY, schema);
    let taos = TaosOptions::new()
        .database(database.clone())
        .build()
        .unwrap();
    for tbname in table_list {
        let tag_buffer = fetch_stable_tag_buffer(database.clone(), tbname.clone()).await;
        let mut res = taos
            .query(format!("select tbname{} from {}", tag_buffer, tbname).as_str())
            .await
            .unwrap();
        let stream = res.block_stream();
        serialize.serialize_tag(&tbname, stream).await;
    }
}

async fn backup_data(
    schema: Arc<Type>,
    tbname_list: Vec<String>,
    mut path: PathBuf,
    database: String,
) {
    let thread_id = thread_id::get();
    path.push(format!("{}.parquet", thread_id));
    let file = File::create(path.as_path()).unwrap();
    let taos = TaosOptions::new().database(database).build().unwrap();
    let mut serialize = Serialize::new(file, Compression::SNAPPY, schema);
    for tbname in tbname_list {
        let mut res = taos
            .query(format!("select * from {}", tbname).as_str())
            .await
            .unwrap();
        let stream = res.block_stream();
        serialize.serialize_data(&tbname, stream).await;
    }
}

use libtaos::Taos;
use parquet::{
    basic::{
        Compression, ConvertedType, LogicalType, Repetition, TimeUnit, TimestampType,
        Type as PhysicalType,
    },
    file::{
        properties::WriterProperties,
        writer::{FileWriter, SerializedFileWriter},
    },
    schema::types::Type,
};
use std::{fs, path::PathBuf, sync::Arc};
use taos::r2d2::TaosPool;
use taos::BlockExt;

pub async fn generate_parquet_schema(taos: &Taos, db: String, stb: &str) -> Arc<Type> {
    let mut fields = vec![];
    taos.use_database(&db).await.unwrap();
    let res = taos.describe(stb).await.unwrap();
    for i in 0..res.cols.len() {
        match res.cols[i].type_ {
            libtaos::TaosDataType::Null => todo!(),
            libtaos::TaosDataType::Bool => fields.push(Arc::new(
                Type::primitive_type_builder(&i.to_string(), PhysicalType::BOOLEAN)
                    .with_repetition(Repetition::REQUIRED)
                    .build()
                    .unwrap(),
            )),
            libtaos::TaosDataType::TinyInt => fields.push(Arc::new(
                Type::primitive_type_builder(&i.to_string(), PhysicalType::INT32)
                    .with_repetition(Repetition::REQUIRED)
                    .with_converted_type(ConvertedType::INT_8)
                    .build()
                    .unwrap(),
            )),
            libtaos::TaosDataType::SmallInt => fields.push(Arc::new(
                Type::primitive_type_builder(&i.to_string(), PhysicalType::INT32)
                    .with_repetition(Repetition::REQUIRED)
                    .with_converted_type(ConvertedType::INT_16)
                    .build()
                    .unwrap(),
            )),
            libtaos::TaosDataType::Int => fields.push(Arc::new(
                Type::primitive_type_builder(&i.to_string(), PhysicalType::INT32)
                    .with_repetition(Repetition::REQUIRED)
                    .build()
                    .unwrap(),
            )),
            libtaos::TaosDataType::BigInt => fields.push(Arc::new(
                Type::primitive_type_builder(&i.to_string(), PhysicalType::INT64)
                    .with_repetition(Repetition::REQUIRED)
                    .build()
                    .unwrap(),
            )),
            libtaos::TaosDataType::Float => fields.push(Arc::new(
                Type::primitive_type_builder(&i.to_string(), PhysicalType::FLOAT)
                    .with_repetition(Repetition::REQUIRED)
                    .build()
                    .unwrap(),
            )),
            libtaos::TaosDataType::Double => fields.push(Arc::new(
                Type::primitive_type_builder(&i.to_string(), PhysicalType::DOUBLE)
                    .with_repetition(Repetition::REQUIRED)
                    .build()
                    .unwrap(),
            )),
            libtaos::TaosDataType::Binary => fields.push(Arc::new(
                Type::primitive_type_builder(&i.to_string(), PhysicalType::BYTE_ARRAY)
                    .with_repetition(Repetition::REQUIRED)
                    .build()
                    .unwrap(),
            )),
            libtaos::TaosDataType::Timestamp => fields.push(Arc::new(
                Type::primitive_type_builder(&i.to_string(), PhysicalType::INT64)
                    .with_repetition(Repetition::REQUIRED)
                    .with_logical_type(Some(LogicalType::TIMESTAMP(TimestampType {
                        is_adjusted_to_u_t_c: false,
                        unit: TimeUnit::MILLIS(Default::default()),
                    })))
                    .build()
                    .unwrap(),
            )),
            libtaos::TaosDataType::NChar => fields.push(Arc::new(
                Type::primitive_type_builder(&i.to_string(), PhysicalType::BYTE_ARRAY)
                    .with_repetition(Repetition::REQUIRED)
                    .with_logical_type(Some(LogicalType::STRING(Default::default())))
                    .build()
                    .unwrap(),
            )),
            libtaos::TaosDataType::UTinyInt => fields.push(Arc::new(
                Type::primitive_type_builder(&i.to_string(), PhysicalType::INT32)
                    .with_repetition(Repetition::REQUIRED)
                    .with_converted_type(ConvertedType::UINT_8)
                    .build()
                    .unwrap(),
            )),
            libtaos::TaosDataType::USmallInt => fields.push(Arc::new(
                Type::primitive_type_builder(&i.to_string(), PhysicalType::INT32)
                    .with_repetition(Repetition::REQUIRED)
                    .with_converted_type(ConvertedType::UINT_16)
                    .build()
                    .unwrap(),
            )),
            libtaos::TaosDataType::UInt => fields.push(Arc::new(
                Type::primitive_type_builder(&i.to_string(), PhysicalType::INT32)
                    .with_repetition(Repetition::REQUIRED)
                    .with_converted_type(ConvertedType::UINT_32)
                    .build()
                    .unwrap(),
            )),
            libtaos::TaosDataType::UBigInt => fields.push(Arc::new(
                Type::primitive_type_builder(&i.to_string(), PhysicalType::INT64)
                    .with_repetition(Repetition::REQUIRED)
                    .with_converted_type(ConvertedType::UINT_64)
                    .build()
                    .unwrap(),
            )),
            libtaos::TaosDataType::Json => todo!(),
            libtaos::TaosDataType::Unknown => todo!(),
        }
    }
    Arc::new(
        Type::group_type_builder("schema")
            .with_fields(&mut fields)
            .build()
            .unwrap(),
    )
}

pub async fn backup_data_parquet(
    pool: TaosPool,
    db: String,
    tb: String,
    schema: Arc<Type>,
    target: PathBuf,
) {
    let props = Arc::new(
        WriterProperties::builder()
            .set_compression(Compression::SNAPPY)
            .build(),
    );
    let mut path = target.clone();
    path.push(format!("{}.parquet", tb));
    let file = fs::File::create(path).unwrap();
    let mut writer = SerializedFileWriter::new(file, schema, props).unwrap();
    let taos = pool.get().unwrap();
    let res = taos
        .query(format!("select * from {}.{} ", db, tb).as_str())
        .await
        .unwrap();
    let stream = res.fetch_block_stream();
    use futures::future;
    use futures::stream::StreamExt;
    stream
        .enumerate()
        .for_each(|(_, partial)| {
            // log::debug!("num of row in block: {}", partial.num_of_rows());
            let mut row_group_writer = writer.next_row_group().unwrap();
            for col in partial.columns_iter() {
                let data_writer = row_group_writer.next_column().unwrap();
                if let Some(mut writer) = data_writer {
                    use taos::block::BorrowedColumn::*;
                    match writer {
                        parquet::column::writer::ColumnWriter::BoolColumnWriter(ref mut typed) => {
                            match col {
                                Bool(is_nulls, values) => {
                                    typed.write_batch(&values, None, None).unwrap();
                                }
                                _ => unreachable!(),
                            }
                        }
                        parquet::column::writer::ColumnWriter::Int32ColumnWriter(ref mut typed) => {
                            // let values;
                            match col {
                                TinyInt(is_nulls, values) => {
                                    let values: Vec<i32> =
                                        values.into_iter().map(|v| *v as _).collect();
                                    typed.write_batch(&values, None, None).unwrap();
                                    // std::mem::transmute::<*const i32>(values.ptr()).

                                    // values = values
                                    //     .into_iter()
                                    //     .map(|v| match v {
                                    //         Some(u) => Some(u as i32),
                                    //         None => None,
                                    //     })
                                    //     .collect::<Option<Vec<i32>>>()
                                    //     .unwrap()
                                }
                                SmallInt(is_nulls, values) => {
                                    let values: Vec<i32> =
                                        values.into_iter().map(|v| *v as _).collect();
                                    typed.write_batch(&values, None, None).unwrap();
                                }
                                Int(is_nulls, values) => {
                                    typed.write_batch(&values, None, None).unwrap();
                                }
                                UTinyInt(is_nulls, values) => {
                                    let values: Vec<i32> =
                                        values.into_iter().map(|v| *v as _).collect();
                                    typed.write_batch(&values, None, None).unwrap();
                                }
                                USmallInt(is_nulls, values) => {
                                    let values: Vec<i32> =
                                        values.into_iter().map(|v| *v as _).collect();
                                    typed.write_batch(&values, None, None).unwrap();
                                }
                                UInt(is_nulls, values) => {
                                    // let values: Vec<i32> = values.into_iter().map(|v| *v as _).collect();
                                    let len = values.len();
                                    let ptr: *mut i32 = values.as_ptr() as _;
                                    let values = unsafe { std::slice::from_raw_parts(ptr, len) };
                                    typed.write_batch(&values, None, None).unwrap();
                                }

                                _ => unreachable!(),
                            }
                            // typed.write_batch(&values, None, None).unwrap();
                        }
                        parquet::column::writer::ColumnWriter::Int64ColumnWriter(ref mut typed) => {
                            // let values;
                            match col {
                                BigInt(is_nulls, values) => {
                                    typed.write_batch(&values, None, None).unwrap();
                                }
                                UBigInt(is_nulls, values) => {
                                    // let values: Vec<i32> = values.into_iter().map(|v| *v as _).collect();
                                    let len = values.len();
                                    let ptr: *mut i64 = values.as_ptr() as _;
                                    let values = unsafe { std::slice::from_raw_parts(ptr, len) };
                                    typed.write_batch(&values, None, None).unwrap();
                                }
                                Timestamp(is_nulls, values) => {
                                    typed.write_batch(&values, None, None).unwrap();
                                }
                                _ => unreachable!(),
                            }
                            // typed.write_batch(&values, None, None).unwrap();
                        }
                        parquet::column::writer::ColumnWriter::FloatColumnWriter(ref mut typed) => {
                            // let values;
                            match col {
                                Float(is_nulls, values) => {
                                    typed.write_batch(&values, None, None).unwrap();
                                }
                                _ => unreachable!(),
                            }
                        }
                        parquet::column::writer::ColumnWriter::DoubleColumnWriter(
                            ref mut typed,
                        ) => match col {
                            Double(is_nulls, values) => {
                                typed.write_batch(&values, None, None).unwrap();
                            }
                            _ => unreachable!(),
                        },
                        parquet::column::writer::ColumnWriter::ByteArrayColumnWriter(
                            ref mut typed,
                        ) => {
                            let mut values = vec![];
                            match col {
                                Binary(v) => {
                                    for f in v.into_iter() {
                                        match f {
                                            Some(u) => values.push(
                                                parquet::data_type::ByteArray::from(u.to_vec()),
                                            ),
                                            None => {
                                                values.push(parquet::data_type::ByteArray::from(""))
                                            }
                                        }
                                    }
                                }
                                NChar(v) => {
                                    for f in v.into_iter() {
                                        match f {
                                            Some(u) => values.push(
                                                parquet::data_type::ByteArray::from(u),
                                            ),
                                            None => {
                                                values.push(parquet::data_type::ByteArray::from(""))
                                            }
                                        }
                                    }
                                }
                                _ => unreachable!(),
                            }
                            typed.write_batch(&values, None, None).unwrap();
                        }
                        _ => unreachable!(),
                    }
                    row_group_writer.close_column(writer).unwrap();
                }
            }
            writer.close_row_group(row_group_writer).unwrap();
            future::ready(())
        })
        .await;
    writer.close().unwrap();
}

use futures::TryStreamExt;
use taos::prelude::*;
use taosx::Database;
use tokio::runtime::Builder;
#[derive(::serde::Deserialize, Debug)]
pub struct Table {
    pub table_name: String,
    pub stable_name: String,
}

pub fn fetch_table_list(database: String) -> (Vec<String>, Vec<String>, Vec<String>) {
    let mut select_list = vec![];
    let mut describe_list = vec![];
    let mut stable_list = vec![];
    let taos = TaosOptions::new().database(database).build().unwrap();
    let tables: Vec<Table> = Builder::new_current_thread()
        .enable_all()
        .build()
        .unwrap()
        .block_on(async {
            taos.query("show tables")
                .await
                .unwrap()
                .deserialize_stream()
                .try_collect()
                .await
                .unwrap()
        });
    for table in tables {
        select_list.push(table.table_name.clone());
        if table.stable_name.is_empty() {
            describe_list.push(table.table_name);
        } else if !describe_list.contains(&table.stable_name) {
            stable_list.push(table.stable_name.clone());
            describe_list.push(table.stable_name);
        }
    }
    (select_list, describe_list, stable_list)
}

pub fn fetch_database_info(database: String) -> Database {
    let taos = TaosOptions::new()
        .database(database.clone())
        .build()
        .unwrap();
    let dbs: Vec<Database> = Builder::new_current_thread()
        .enable_all()
        .build()
        .unwrap()
        .block_on(async {
            taos.query("show databases")
                .await
                .unwrap()
                .deserialize_stream()
                .try_collect()
                .await
                .unwrap()
        });
    for db in dbs {
        if db.name == database {
            return db;
        }
    }
    panic!("cannot find database {}", database);
}

pub async fn fetch_stable_tag_buffer(database: String, stable: String) -> String {
    let mut tag_buffer = String::from("");
    let taos = TaosOptions::new()
        .database(database.clone())
        .build()
        .unwrap();
    let describe = taos
        .describe(format!("{}.{}", database.clone(), stable).as_str())
        .await
        .unwrap();
    for col in describe {
        if col.is_tag() {
            tag_buffer += ",";
            tag_buffer += col.field();
        }
    }
    tag_buffer
}

use parquet::basic::{Repetition, Type as PhysicalType};
use parquet::schema::types::Type;
use std::sync::Arc;

#[derive(Debug, Clone, Default)]
pub struct TaosParquetSchema {
    fields: Vec<Arc<Type>>,
}

impl TaosParquetSchema {
    fn add_required_byte_array_col(mut self, name: &str) -> Self {
        self.fields.push(Arc::new(
            Type::primitive_type_builder(name, PhysicalType::BYTE_ARRAY)
                .with_repetition(Repetition::REQUIRED)
                .build()
                .unwrap(),
        ));
        self
    }

    fn add_group(mut self, group: Arc<Type>) -> Self {
        self.fields.push(group);
        self
    }

    fn build_repeated_group(mut self, name: &str) -> Arc<Type> {
        Arc::new(
            Type::group_type_builder(name)
                .with_fields(&mut self.fields)
                .with_repetition(Repetition::REPEATED)
                .build()
                .unwrap(),
        )
    }

    fn build_schema(mut self, name: &str) -> Arc<Type> {
        Arc::new(
            Type::group_type_builder(name)
                .with_fields(&mut self.fields)
                .build()
                .unwrap(),
        )
    }

    pub fn build(self) -> Arc<Type> {
        self.add_group(
            TaosParquetSchema::default()
                .add_required_byte_array_col("data")
                .build_repeated_group("table_info"),
        )
        .build_schema("table")
    }
}

