pub fn get_parquet_files<'a>(path: PathBuf) -> Vec<String> {
    let mut file_list = vec![];
    let paths = glob(format!("{}/*.parquet", path.as_os_str().to_str().unwrap()).as_str()).unwrap();
    for entry in paths {
        match entry {
            Ok(path) => {
                let filename = path.to_str().unwrap().split("/").last().unwrap();
                file_list.push(filename.to_string())
            }
            Err(e) => println!("{:?}", e),
        }
    }
    file_list
}

fn read<T>(mut column_reader: ColumnReaderImpl<T>, num_rows: usize) -> Vec<T::T>
where
    T: DataType,
    T::T: Default + Copy,
{
    let mut data = vec![];
    const BATCH_SIZE: usize = 100;
    let mut values = [T::T::default(); BATCH_SIZE];
    for _ in 0..(num_rows + BATCH_SIZE - 1) / BATCH_SIZE {
        let (num, _) = column_reader
            .read_batch(BATCH_SIZE, None, None, &mut values)
            .unwrap();
        data.extend(&values[0..num]);
    }
    data
}

fn read_str(mut column_reader: ColumnReaderImpl<ByteArrayType>, num_rows: usize) -> Vec<ByteArray> {
    let mut data = vec![];
    const BATCH_SIZE: usize = 10;
    for _ in 0..(num_rows + BATCH_SIZE - 1 / BATCH_SIZE) {
        let mut values = vec![];
        for _ in 0..BATCH_SIZE {
            values.push(ByteArray::new());
        }
        column_reader
            .read_batch(BATCH_SIZE, None, None, &mut values)
            .unwrap();
        data.extend(values);
    }
    let _ = data.split_off(num_rows);
    log::debug!("data: {:?}", data);
    data
}

pub async fn restore_parquet(pool: TaosPool, path: PathBuf, filename: String, database: String) {
    let taos = pool.get().unwrap();
    taos.query(format!("use {}", database)).await.unwrap();
    let tb = filename.split(".").next().unwrap();
    let parquet_reader =
        SerializedFileReader::try_from(format!("{}/{}.parquet", path.to_str().unwrap(), tb))
            .unwrap();
    let read_schema = parquet_reader.metadata().file_metadata().schema();
    let mut sql = format!("insert into {} values(", tb);
    let fields = read_schema.get_fields();
    let column_num = fields.len();
    for i in 0..column_num {
        if i != column_num - 1 {
            sql += "?,";
        } else {
            sql += "?)";
        }
    }

    for row_group in 0..parquet_reader.num_row_groups() {
        let row_group_reader = parquet_reader.get_row_group(row_group).unwrap();
        let blocks: Vec<_> = (0..row_group_reader.num_columns())
            .map(|col_num| {
                let col_reader = row_group_reader.get_column_reader(col_num).unwrap();
                let row_num = row_group_reader.metadata().num_rows() as usize;
                let nulls = BitVec::zeros(row_num);

                match col_reader {
                    ColumnReader::BoolColumnReader(v) => {
                        let column = read(v, row_num);
                        Block::Bool(nulls, column)
                    }
                    ColumnReader::ByteArrayColumnReader(v) => {
                        let column = read_str(v, row_num);
                        match fields.get(col_num).unwrap().get_basic_info().logical_type() {
                            Some(_) => Block::NChar(
                                column
                                    .into_iter()
                                    .map(|v| {
                                        Some(std::str::from_utf8(v.data()).unwrap().to_string())
                                    })
                                    .collect(),
                            ),
                            None => Block::Binary(
                                column
                                    .into_iter()
                                    .map(|v| Some(v.data().to_vec()))
                                    .collect::<Vec<Option<Vec<u8>>>>()
                                    .to_vec(),
                            ),
                        }
                    }
                    ColumnReader::DoubleColumnReader(v) => {
                        let column = read(v, row_num);
                        Block::Double(nulls, column)
                    }
                    ColumnReader::FloatColumnReader(v) => {
                        let column = read(v, row_num);
                        Block::Float(nulls, column)
                    }
                    ColumnReader::Int32ColumnReader(v) => {
                        let column = read(v, row_num);

                        let values = match fields
                            .get(col_num)
                            .unwrap()
                            .get_basic_info()
                            .converted_type()
                        {
                            ConvertedType::UINT_8 => Block::UTinyInt(
                                nulls,
                                column.into_iter().map(|v| v as u8).collect::<Vec<u8>>(),
                            ),
                            ConvertedType::UINT_16 => Block::USmallInt(
                                nulls,
                                column.into_iter().map(|v| v as u16).collect::<Vec<u16>>(),
                            ),
                            ConvertedType::UINT_32 => Block::UInt(
                                nulls,
                                column.into_iter().map(|v| v as u32).collect::<Vec<u32>>(),
                            ),
                            ConvertedType::INT_8 => Block::TinyInt(
                                nulls,
                                column.into_iter().map(|v| v as i8).collect::<Vec<i8>>(),
                            ),
                            ConvertedType::INT_16 => Block::SmallInt(
                                nulls,
                                column.into_iter().map(|v| v as i16).collect::<Vec<i16>>(),
                            ),
                            ConvertedType::NONE => Block::Int(nulls, column),
                            _ => unreachable!(),
                        };
                        values
                    }
                    ColumnReader::Int64ColumnReader(v) => {
                        let column = read(v, row_num);
                        match fields.get(col_num).unwrap().get_basic_info().logical_type() {
                            Some(_) => Block::Timestamp(nulls, column),
                            None => match fields
                                .get(col_num)
                                .unwrap()
                                .get_basic_info()
                                .converted_type()
                            {
                                ConvertedType::NONE => Block::BigInt(nulls, column),
                                ConvertedType::UINT_64 => Block::UBigInt(
                                    nulls,
                                    column.into_iter().map(|v| v as u64).collect::<Vec<u64>>(),
                                ),
                                _ => unreachable!(),
                            },
                        }
                    }
                    _ => unreachable!(),
                }
            })
            .collect();

        let bind: Vec<_> = blocks.iter().map(|b| b.into()).collect();
        let mut stmt = taos.stmt(&sql).unwrap();
        stmt.multi_bind(&bind).unwrap();
        stmt.execute().unwrap();
    }
}

use anyhow::Result;
use clap::Args;
use futures::prelude::*;
use taos::query::Dsn;
use taosx::plugins::sink::taos::TaosSinkBuilder;
use taosx::plugins::source::taos::TaosSourceBuilder;
use taosx::stream::source::XSourceBuilder;
use taosx::stream::stream::XSinkBuilder;

use taosx::stream::transformer::Action;
use taosx::TaosOpts;

#[derive(Debug, Args)]
/// Import external files to TDengine.
pub(crate) struct App {
    #[clap(short, long)]
    /// A DSN(database source name) format string for source TDengine: taos:///db1, for eg.
    from: Dsn,
    #[clap(short, long)]
    /// A DSN(database source name) format string for target TDengine: taos:///db2, for eg.
    to: Dsn,

    /// Transformer actions.
    ///
    /// Supported action format:
    ///
    /// - 'add-tag:tag1=value1': add a tag named `tag1`, and valued `value1`.
    /// - 'select:subset:a,b,c': select three columns: `a`, `b`, `c`
    /// - 'select:exclude:a,b,c': select all columns except the three: `a`, `b`, `c`
    /// - 'rename-table:prefix:v1_': rename all tables as `v1_{{ name }}`
    /// - 'rename-super-table:suffix:_stb': rename all super tables as suffixed '_stb'
    /// - 'rename-child-stable:template:prefix_{{ name }}_stb': rename all super tables with prefix 'prefix_' and suffix '_stb'
    #[clap(short = 'T', long)]
    transform: Vec<Action>,
    /// Number of workers for TMQ consumers.
    #[clap(short = 'j', long)]
    workers: Option<usize>,
}

impl App {
    pub async fn run_with_taos_opts(self, _opts: &TaosOpts) -> Result<()> {
        log::debug!("app: {self:?}");

        let mut source_builder = TaosSourceBuilder::from_dsn(self.from)?;
        let max_workers = source_builder.max_workers();

        let sink_builder = TaosSinkBuilder::from_dsn(self.to)?.with_transformer(self.transform);

        let mut workers = self.workers.unwrap_or(max_workers);
        if max_workers != 0 && workers > max_workers {
            log::warn!("maximum workers for the stream is {max_workers} while you want {workers}, reduce to limit");
            workers = max_workers;
        }
        if workers == 0 {
            workers = 1;
        }
        let mut handlers = Vec::new();
        log::info!("use {workers} workers (max: {max_workers})");

        for _ in 0..workers {
            let source = source_builder.build_source()?;
            let sink = sink_builder.build_sink()?;
            handlers.push(tokio::spawn(async move { source.forward(sink).await }));
        }

        for hd in handlers {
            hd.await??;
        }

        let summary = sink_builder.summary();

        log::info!(
            "Summary: total synced {} blocks with {} rows",
            summary.blocks(),
            summary.rows()
        );
        Ok(())
    }
}

mod commands;
use clap::{Parser, Subcommand};
use log::Level;
use pretty_env_logger::env_logger::fmt::Color;
use std::io::prelude::*;
use taosx::TaosOpts;

#[derive(Parser, Debug)]
#[clap(author, version, about)]
struct Cli {
    #[clap(flatten)]
    options: TaosOpts,
    #[clap(short, long)]
    log_level: Option<log::LevelFilter>,
    #[clap(subcommand)]
    command: Commands,
}

#[derive(Debug, Subcommand)]
enum Commands {
    Export(commands::export::App),
    Import(commands::import::App),
    Backup(commands::backup::App),
    Restore(commands::restore::App),
    Sync(commands::sync::App),
    #[clap(external_subcommand)]
    External(Vec<String>),
}

pub fn cli<'help>() -> clap::Command<'help> {
    clap::Command::new("taosx")
}

#[tokio::main(flavor = "multi_thread", worker_threads = 10)]
async fn main() -> anyhow::Result<()> {
    let cli = Cli::parse();

    let mut builder = pretty_env_logger::formatted_timed_builder();
    if let Some(level) = cli.log_level {
        builder.filter_level(level);
    } else if let Ok(s) = ::std::env::var("RUST_LOG") {
        builder.parse_filters(&s);
    } else {
        builder.filter_level(log::LevelFilter::Info);
    }
    builder
        .format_module_path(true)
        .format(|buf, record| -> std::result::Result<(), std::io::Error> {
            fn colored_level<'a>(
                style: &'a mut pretty_env_logger::env_logger::fmt::Style,
                level: Level,
            ) -> pretty_env_logger::env_logger::fmt::StyledValue<'a, &'static str> {
                match level {
                    Level::Trace => style.set_color(Color::Magenta).value("TRACE"),
                    Level::Debug => style.set_color(Color::Blue).value("DEBUG"),
                    Level::Info => style.set_color(Color::Green).value("INFO"),
                    Level::Warn => style.set_color(Color::Yellow).value("WARN "),
                    Level::Error => style.set_color(Color::Red).value("ERROR"),
                }
            }
            let mut style = buf.style();
            let level = colored_level(&mut style, record.level());
            let mut mod_path = buf.style();

            let mod_path = mod_path.set_bold(true).value(format!(
                "{}:{}",
                record.file().unwrap_or("unknown"),
                record.line().unwrap_or(0),
            ));
            writeln!(
                buf,
                "[{:29} {: <5}] {} > {}",
                chrono::Local::now().format("%Y-%m-%dT%H:%M:%S.%f"),
                level,
                mod_path,
                record.args()
            )
        })
        // .is_test(true)
        .init();
    let command = cli.command;

    // You can check for the existence of subcommands, and if found use their
    // matches just as you would the top level cmd
    match command {
        Commands::Export(app) => {
            app.run_with_taos_opts(&cli.options);
        }
        Commands::Import(app) => {
            app.run_with_taos_opts(&cli.options);
        }
        Commands::Backup(app) => {
            app.run_with_taos_opts(&cli.options);
        }
        Commands::Restore(app) => {
            app.run_with_taos_opts(&cli.options);
        }
        Commands::Sync(app) => {
            app.run_with_taos_opts(&cli.options).await?;
        }

        Commands::External(args) => {
            let (name, args) = args.split_first().unwrap();
            println!("Call external plugin taosx-{name}: {args:?}");
            let cmd = format!("taosx-{name}");
            std::process::Command::new(&cmd)
                .args(args)
                .spawn()
                .expect(&format!("unable to run command {cmd}"));
        }
    }

    Ok(())
}

