#[derive(Debug, Deserialize, Serialize, Clone)]
pub struct TaosTag {
    pub name: String,
    pub tags: Vec<Vec<Value>>,
}

impl TaosTag {
    pub fn new<'b, T: BlockExt>(name: String, iter: taos::query::RowsIter<'b, T>) -> Self {
        let mut values = vec![];
        for row in iter {
            let mut tmp = vec![];
            for bv in row {
                tmp.push(bv.1.into_value());
            }
            values.push(tmp);
        }
        Self { name, tags: values }
    }
}

