}

impl TaosBlock {
    pub fn new<'b>(name: String, iter: impl Iterator<Item = BorrowedColumn<'b>>) -> Self {
        let mut data = vec![];
        for col in iter {
            data.push(TaosColumn::new(col.into_owned()));
        }
        Self { name, data }
    }

    pub fn to_column_vec(&self) -> Vec<Column> {
        self.data.to_vec().iter().map(|v| v.to_column()).collect()
    }

