        stmt.multi_bind(&bind)?;
        stmt.execute()?;
        let inserted = stmt.affected_rows();
        self.metrics.blocks.fetch_add(1, Ordering::SeqCst);
        self.metrics.rows.fetch_add(inserted, Ordering::SeqCst);
        log::info!("[{idx}] inserted {inserted} rows into {table}");
        Ok(())
    }

