fn test_r2d2() {
    use crate::prelude::*;
    use std::sync::mpsc::channel;
    use std::thread;

    let opts = TaosOptions::new();
    let pool = TaosPool::builder().build(opts).expect("");
    let (tx, rx) = channel();
    for _ in 0..4 {
        let tx = tx.clone();
        let pool = pool.clone();
        thread::spawn(move || {
            let taos = pool.get().unwrap();
            let _ = taos.query_sync("show connections").unwrap().affected_rows();
