    fn variant_seed<V>(self, seed: V) -> Result<(V::Value, Self::Variant), Self::Error>
    where
        V: DeserializeSeed<'de>,
    {
        return seed
            .deserialize(self.value.ty().as_variant_str().into_deserializer())
            .map(|v| (v, self));
    }
}

