    #[test]
    fn value_de_value() {
        use std::cmp::PartialEq;
        use Value::*;
        macro_rules! _de_value {
            ($($v:expr) *) => {
                $(
                    {
                        let v = $v;
                        let d = Value::deserialize(v.clone().into_deserializer()).expect("");
                        assert!(v.eq(&d));
                    }
                )*
            }
        }
        _de_value!(
                Null Bool(true) TinyInt(0xf) SmallInt(0xfff) Int(0xffff) BigInt(-1) Float(1.0) Double(1.0)
                UTinyInt(0xf) USmallInt(0xfff) UInt(0xffff) UBigInt(0xffffffff)
                Timestamp(crate::common::timestamp::Timestamp::Milliseconds(0)) VarChar("anything".to_string())
                NChar("".to_string()) VarBinary(vec![1,2,3]) Blob(vec![1,2, 3]) MediumBlob(vec![1,2,3])
                Json(serde_json::json!({"name": "ABC"}))
        );
    }

    #[test]
    fn de_value_as_inner() {
        use Value::*;
        macro_rules! _de_value {
            ($($v:expr, $ty:ty, $tv:expr) *) => {
                $(
                    {
                        let d = <$ty>::deserialize($v.into_deserializer()).expect("");
                        assert_eq!(d, $tv);
                    }
                )*
            }
        }

        _de_value!(
            Null, Option<u8>, None
            TinyInt(-1), i8, -1
            SmallInt(-1), i16, -1
            Int(0x0fff_ffff), i32, 0x0fff_ffff
            BigInt(0xffffffff), i64, 0xffffffff
            UTinyInt(0xff), u8, 0xff
            USmallInt(0xffff), u16, 0xffff
            UInt(0x_ffff_ffff), u32, 0x_ffff_ffff
            UBigInt(0x_ffff_ffff_ffff_ffff), u64, 0x_ffff_ffff_ffff_ffff
            Float(1.0), f32, 1.0
            Double(f64::MAX), f64, f64::MAX
            VarChar("".to_string()), String, "".to_string()
            NChar("".to_string()), String, "".to_string()
            Timestamp(crate::Timestamp::Milliseconds(1)), crate::Timestamp, crate::Timestamp::Milliseconds(1)
            VarBinary(vec![0, 1,2]), Vec<u8>, vec![0, 1, 2]
            Blob(vec![0, 1,2]), Vec<u8>, vec![0, 1, 2]
            MediumBlob(vec![0, 1,2]), Vec<u8>, vec![0, 1, 2]
        );
    }

    #[test]
    fn de_str() {
        use serde_json::json;
        use Value::*;

        macro_rules! _de_str {
            ($v:expr, is_err) => {
                assert!(String::deserialize($v.into_deserializer()).is_err());
            };
            ($v:expr, $tv:expr) => {
                assert_eq!(String::deserialize($v.into_deserializer()).expect("str"), $tv);
            };
            ($($v:expr, $c:ident) *) => {
                $(
                    {
                        assert!(String::deserialize($v.into_deserializer()).$c());
                    }
                )*
            };
            ($($v2:expr, is_err) +; $($v:expr, $tv:expr) + ) => {
                $(
                    _de_str!($v2, is_err);
                )*
                $(
                    _de_str!($v, $tv);
                )*
            }
        }
        _de_str! {
            TinyInt(-1), is_err
            SmallInt(-1), is_err
            Int(-1), is_err
            BigInt(-1), is_err
            UTinyInt(1), is_err
            USmallInt(1), is_err
            UInt(1), is_err
            UBigInt(1), is_err
            ;

            Null, ""
            Timestamp(crate::Timestamp::Milliseconds(0)), "1970-01-01T00:00:00"
            VarChar("String".to_string()), "String"
            VarChar("".to_string()), ""
            Json(json!("abc")), json!("abc").to_string()
            Json(json!({ "name": "abc"})), json!({ "name": "abc"}).to_string()
            Json(json!(1)), json!(1).to_string()
            Json(json!(null)), json!(null).to_string()
        };
    }

    #[test]
    fn de_json() {
        use Value::*;

        macro_rules! _de_json {
            ($v:expr, $ty:ty, $tv:expr) => {{
                let v = Json($v);
                let d = <$ty>::deserialize(v.clone().into_deserializer()).expect("de json");
                assert_eq!(d, $tv);
            }};
        }

        _de_json!(
            serde_json::json!("string"),
            String,
            "\"string\"".to_string()
        );

        #[derive(Debug, PartialEq, Eq, Deserialize)]
        struct Obj {
            name: String,
        }
        _de_json!(
            serde_json::json!({ "name": "string" }),
            Obj,
            Obj {
                name: "string".to_string()
            }
        );
    }

    #[test]
    fn de_newtype_struct() {
        use serde_json::json;
        use Value::*;

        macro_rules! _de_ty {
            ($v:expr, $ty:ty, $tv:expr) => {{
                let d = <$ty>::deserialize($v.into_deserializer()).expect("de type");
                assert_eq!(d, $tv);
            }};
        }
        #[derive(Debug, PartialEq, Eq, Deserialize)]
        struct JsonStr(String);
        _de_ty!(
            Json(json!("string")),
            JsonStr,
            JsonStr("string".to_string())
        );

        #[derive(Debug, PartialEq, Eq, Deserialize)]
        struct Primi<T>(T);
        _de_ty!(Json(json!(1)), Primi<i32>, Primi(1));
        _de_ty!(TinyInt(1), Primi<i8>, Primi(1));
        _de_ty!(SmallInt(1), Primi<i64>, Primi(1));
        _de_ty!(Int(1), Primi<i64>, Primi(1));
        _de_ty!(BigInt(1), Primi<i64>, Primi(1));

        macro_rules! _de_prim {
            ($v:ident, $ty:ty, $inner:literal) => {
                log::debug!(
                    "ty: {}, prim: {}",
                    stringify!($v),
                    std::any::type_name::<$ty>()
                );
                _de_ty!($v($inner), Primi<$ty>, Primi($inner));
            };
        }

        macro_rules! _de_prim_cross {
            ($($vty:ident) +, $tt:ty) => {
                $(
                  _de_prim!($vty, $tt, 1);
                )*
            };
            ($($ty:ty) +) => {
                $(
                    _de_prim_cross!(TinyInt SmallInt Int BigInt UTinyInt USmallInt UInt UBigInt, $ty);
                )*
            };
            () => {
                _de_prim_cross!(i8 i16 i32 i64 u8 u16 u32 u64);
            };
        }
        _de_prim_cross!();
    }
}

use std::any::type_name;
use std::marker::PhantomData;

use serde::de::{DeserializeSeed, IntoDeserializer, MapAccess, SeqAccess, Visitor};
use serde::Deserializer;

use serde::de::value::Error;

use crate::common::BorrowedValue;
use crate::Field;

/// Row-based deserializer helper.
///
/// 'b: field lifetime may go across the whole query.
pub(crate) struct RecordDeserializer<'b, R>
where
    R: IntoIterator<Item = (&'b Field, BorrowedValue<'b>)>,
{
    inner: <R as IntoIterator>::IntoIter,
    value: Option<BorrowedValue<'b>>,
    _marker: PhantomData<&'b u8>,
}

impl<'b, R> From<R> for RecordDeserializer<'b, R>
where
    R: IntoIterator<Item = (&'b Field, BorrowedValue<'b>)>,
{
    fn from(input: R) -> Self {
        Self {
            inner: input.into_iter(),
            value: None,
            _marker: PhantomData,
        }
    }
}

impl<'b, R> RecordDeserializer<'b, R>
where
    R: IntoIterator<Item = (&'b Field, BorrowedValue<'b>)>,
{
    fn next_value(&mut self) -> Option<BorrowedValue<'b>> {
        self.inner.next().map(|(_, v)| v)
    }
}

impl<'de, 'b: 'de, R> MapAccess<'de> for RecordDeserializer<'b, R>
where
    R: IntoIterator<Item = (&'b Field, BorrowedValue<'b>)>,
{
    type Error = Error;

    fn next_key_seed<K>(&mut self, seed: K) -> Result<Option<K::Value>, Self::Error>
    where
        K: DeserializeSeed<'de>,
    {
        match self.inner.next() {
            Some((field, value)) => {
                self.value = Some(value);
                let field = &*field;
                seed.deserialize(field.name().into_deserializer()).map(Some)
            }
            _ => Ok(None),
        }
    }

    fn next_value_seed<V>(&mut self, seed: V) -> Result<V::Value, Self::Error>
    where
        V: DeserializeSeed<'de>,
    {
        let value = self.value.take().unwrap(); // always be here, so it's safe to unwrap

        log::trace!("target value: {:?}", type_name::<V::Value>());
        seed.deserialize(value)
            .map_err(<Self::Error as serde::de::Error>::custom)
    }
}

impl<'de, 'b: 'de, R> SeqAccess<'de> for RecordDeserializer<'b, R>
where
    R: IntoIterator<Item = (&'b Field, BorrowedValue<'b>)>,
{
    type Error = Error;

    fn next_element_seed<S>(&mut self, seed: S) -> Result<Option<S::Value>, Self::Error>
    where
        S: DeserializeSeed<'de>,
    {
        match self.inner.next() {
            Some((_, v)) => seed
                .deserialize(v)
                .map_err(<Self::Error as serde::de::Error>::custom)
                .map(Some),
            None => Ok(None),
        }
    }
}

impl<'de, 'b: 'de, R> Deserializer<'de> for RecordDeserializer<'b, R>
where
    R: IntoIterator<Item = (&'b Field, BorrowedValue<'b>)>,
{
    type Error = Error;

    // Look at the input data to decide what Serde data model type to
    // deserialize as. Not all data formats are able to support this operation.
    // Formats that support `deserialize_any` are known as self-describing.
    fn deserialize_any<V>(mut self, visitor: V) -> Result<V::Value, Self::Error>
    where
        V: Visitor<'de>,
    {
        log::trace!("call deserialize any for <{}>", type_name::<V>());
        match self.next_value() {
            Some(v) => v
                .deserialize_any(visitor)
                .map_err(<Self::Error as serde::de::Error>::custom),
            None => Err(<Self::Error as serde::de::Error>::custom(
                "expect value, not none",
            )),
        }
    }

    serde::forward_to_deserialize_any! {
        bool i8 i16 i32 i64 u8 u16 u32 u64 f32 f64 char bytes byte_buf enum
        identifier ignored_any
    }

    // Refer to the "Understanding deserializer lifetimes" page for information
    // about the three deserialization flavors of strings in Serde.
    fn deserialize_str<V>(mut self, visitor: V) -> Result<V::Value, Self::Error>
    where
        V: Visitor<'de>,
    {
        log::trace!("call deserialize_str for <{}>", type_name::<V>());
        match self.next_value() {
            Some(v) => v
                .deserialize_str(visitor)
                .map_err(<Self::Error as serde::de::Error>::custom),
            None => Err(<Self::Error as serde::de::Error>::custom(
                "expect value, not none",
            )),
        }
    }

    fn deserialize_string<V>(self, visitor: V) -> Result<V::Value, Self::Error>
    where
        V: Visitor<'de>,
    {
        self.deserialize_str(visitor)
    }

    fn deserialize_option<V>(mut self, visitor: V) -> Result<V::Value, Self::Error>
    where
        V: Visitor<'de>,
    {
        log::debug!("call deserialize_option for <{}>", type_name::<V>());
        match self.next_value() {
            Some(v) => {
                if v.is_null() {
                    visitor.visit_none()
                } else {
                    visitor
                        .visit_some(v)
                        .map_err(<Self::Error as serde::de::Error>::custom)
                }
            }
            _ => Err(<Self::Error as serde::de::Error>::custom(
                "expect next value",
            )),
        }
    }

    // In Serde, unit means an anonymous value containing no data.
    fn deserialize_unit<V>(mut self, visitor: V) -> Result<V::Value, Self::Error>
    where
        V: Visitor<'de>,
    {
        match self.next_value() {
            Some(_v) => visitor.visit_unit(),
            _ => Err(<Self::Error as serde::de::Error>::custom(
                "there's no enough value",
            )),
        }
    }

    // Unit struct means a named value containing no data.
    fn deserialize_unit_struct<V>(
        self,
        _name: &'static str,
        visitor: V,
    ) -> Result<V::Value, Self::Error>
    where
        V: Visitor<'de>,
    {
        self.deserialize_unit(visitor)
    }

    // As is done here, serializers are encouraged to treat newtype structs as
    // insignificant wrappers around the data they contain. That means not
    // parsing anything other than the contained value.
    fn deserialize_newtype_struct<V>(
        self,
        _name: &'static str,
        visitor: V,
    ) -> Result<V::Value, Self::Error>
    where
        V: Visitor<'de>,
    {
        log::debug!("deserialize_newtype_struct: {}", _name);
        visitor.visit_newtype_struct(self)
    }

    // Deserialization of compound types like sequences and maps happens by
    // passing the visitor an "Access" object that gives it the ability to
    // iterate through the data contained in the sequence.
    fn deserialize_seq<V>(self, visitor: V) -> Result<V::Value, Self::Error>
    where
        V: Visitor<'de>,
    {
        visitor.visit_seq(self)
    }

    // Tuples look just like sequences.
    fn deserialize_tuple<V>(self, _len: usize, visitor: V) -> Result<V::Value, Self::Error>
    where
        V: Visitor<'de>,
    {
        // self.deserialize_any(visitor)
        visitor.visit_seq(self)
    }

    // Tuple structs look just like sequences.
    fn deserialize_tuple_struct<V>(
        self,
        _name: &'static str,
        _len: usize,
        visitor: V,
    ) -> Result<V::Value, Self::Error>
    where
        V: Visitor<'de>,
    {
        self.deserialize_seq(visitor)
    }

    // Much like `deserialize_seq` but calls the visitors `visit_map` method
    // with a `MapAccess` implementation, rather than the visitor's `visit_seq`
    // method with a `SeqAccess` implementation.
    fn deserialize_map<V>(self, visitor: V) -> Result<V::Value, Self::Error>
    where
        V: Visitor<'de>,
    {
        // let value = visitor.visit_map(self);
        // unimplemented!();
        log::trace!("visit map for {}", type_name::<V::Value>());
        visitor.visit_map(self)
    }

    // Structs look just like maps in JSON.
    //
    // Notice the `fields` parameter - a "struct" in the Serde data model means
    // that the `Deserialize` implementation is required to know what the fields
    // are before even looking at the input data. Any key-value pairing in which
    // the fields cannot be known ahead of time is probably a map.
    fn deserialize_struct<V>(
        self,
        _name: &'static str,
        _fields: &'static [&'static str],
        visitor: V,
    ) -> Result<V::Value, Self::Error>
    where
        V: Visitor<'de>,
    {
        log::debug!("name: {_name}, fields: {_fields:?}");
        self.deserialize_map(visitor)
    }
}

use chrono::NaiveDateTime;
use paste::paste;
use serde::{Deserialize, Serialize};

use std::{fmt::Display, str::FromStr};

use crate::common::Precision;

#[derive(Debug, Default, PartialEq, Eq, Serialize, Deserialize)]
pub struct DatabaseProperties {
    pub vgroups: Option<u64>,
    pub replica: Option<u16>,
    pub quorum: Option<u16>,
    pub days: Option<u16>,
    pub keep: Option<String>,
    #[serde(rename = "cache(MB)")]
    pub cache: Option<u32>,
    pub blocks: Option<u32>,
    pub minrows: Option<u32>,
    pub maxrows: Option<u32>,
    #[serde(rename = "wallevel")]
    pub wal: Option<u8>,
    pub fsync: Option<u32>,
    pub comp: Option<u8>,
    pub cachelast: Option<u8>,
    pub precision: Option<Precision>,
    pub update: Option<u8>,
}

macro_rules! _prop_builder {
    ($($f:ident)*, $ty:ty) => {
        $(pub fn $f(mut self, $f: $ty) -> Self {
            self.$f = Some($f);
            self
        })*
    };
}
impl DatabaseProperties {
    pub fn new() -> Self {
        Self::default()
    }

    _prop_builder!(vgroups, u64);
    _prop_builder!(cache blocks minrows maxrows fsync, u32);
    _prop_builder!(replica quorum days, u16);
    _prop_builder!(wal comp cachelast update, u8);
    _prop_builder!(precision, Precision);
    _prop_builder!(keep, String);
}

impl Display for DatabaseProperties {
    #[inline]
    #[allow(unused_assignments)]
    fn fmt(&self, f: &mut std::fmt::Formatter<'_>) -> std::fmt::Result {
        let mut has_wrote = false;

        macro_rules! _write_if {
            ($($f:ident) *) => {
                $(if let Some($f) = &self.$f {
                    if has_wrote {
                        write!(f, " {} {}", paste!(stringify!([<$f:upper>])), $f)?;
                    } else {
                        write!(f, "{} {}", paste!(stringify!([<$f:upper>])), $f)?;
                        has_wrote = true;
                    }
                })*
            };
            ('str $($s:ident) *) => {
                $(if let Some($s) = &self.$s {
                    if has_wrote {
                        write!(f, " {} '{}'", paste!(stringify!([<$s:upper>])), $s)?;
                    } else {
                        write!(f, "{} '{}'", paste!(stringify!([<$s:upper>])), $s)?;
                        has_wrote = true;
                    }
                })*
            };
            ($($f:ident) *; 'str $($s:ident) *) => {
                _write_if!($($f) *);
                _write_if!('str $($s) *)
            };
            ($($f:ident) *; 'str $($s:ident) *; $($f2:ident) *) => {
                _write_if!($($f) *; 'str $($s) *);
                _write_if!($($f2) *)
            };
        }

        // todo: keep now may fail
        _write_if!(vgroups replica quorum days keep cache blocks minrows
                   maxrows wal fsync comp cachelast; 'str precision; update);
        Ok(())
    }
}

impl FromStr for DatabaseProperties {
    type Err = anyhow::Error;

    fn from_str(s: &str) -> Result<Self, Self::Err> {
        // CREATE DATABASE log REPLICA 1 QUORUM 1 DAYS 10 KEEP 30 CACHE 1 BLOCKS 3 MINROWS 100 MAXROWS 4096 WAL 1 FSYNC 3000 COMP 2 CACHELAST 0 PRECISION 'us' UPDATE 0
        use nom::branch::alt;
        use nom::character::complete::*;
        use nom::character::complete::{multispace0, multispace1};
        use nom::character::streaming;
        use nom::multi::many0;
        use nom::sequence::*;
        use nom::{bytes::complete::tag, IResult};

        let mut repr = Self::new();

        fn parse_name(s: &str) -> IResult<&str, &str> {
            preceded(
                tuple((multispace0, tag("CREATE DATABASE"), multispace1)),
                alphanumeric1,
            )(s)
        }

        let s = parse_name(s).map(|s| s.0).unwrap_or(s);

        fn parse_props(s: &str) -> IResult<&str, Vec<(&str, &str)>> {
            many0(separated_pair(
                preceded(multispace0, alphanumeric1),
                streaming::char(' '),
                alt((
                    alphanumeric1,
                    delimited(char('\''), alphanumeric1, char('\'')),
                )),
            ))(s)
        }

        if let Ok((_s, props)) = dbg!(parse_props(s)) {
            for (prop, value) in props {
                macro_rules! _parse {
                    ($($($f:ident) +, $t:ident);*) => {
                        paste::paste! {
                            match prop.to_lowercase() {
                                $($(s if s == stringify!($f) => {
                                    repr = repr.$f($t::from_str(value)?);
                                },)*)*
                                _ => (),
                            }
                        }
                    }
                }
                _parse!(vgroups, u64;
                        cache blocks minrows maxrows fsync, u32;
                        replica quorum days, u16;
                        wal comp cachelast  update, u8;
                        keep, String;
                        precision, Precision);
            }
            Ok(repr)
        } else {
            Ok(repr)
        }
    }
}

#[test]
fn db_prop_from_str() {
    let s = "REPLICA 1 QUORUM 1 DAYS 10 KEEP 30 CACHE 1 BLOCKS 3 MINROWS 100 MAXROWS 4096 WAL 1 FSYNC 3000 COMP 2 CACHELAST 0 PRECISION 'us' UPDATE 0";

    let db = DatabaseProperties::from_str(s).unwrap();

    let t = db.to_string();

    dbg!(db);

    assert_eq!(s, t);
}

