fn box_into_raw<T>(v: T) -> *mut T {
    Box::into_raw(Box::new(v))
}

impl<T> From<&T> for TaosBindV2
where
    T: IsValue,
{
    #[inline(always)]
    fn from(v: &T) -> Self {
        macro_rules! as_is {
            () => {
                Self::from_primitive(v)
            };
        }

        match T::TY {
            Ty::Null => Self::null(),
            Ty::Bool => as_is!(),
            Ty::TinyInt => as_is!(),
            Ty::SmallInt => as_is!(),
            Ty::Int => as_is!(),
            Ty::BigInt => as_is!(),
            Ty::UTinyInt => as_is!(),
            Ty::USmallInt => as_is!(),
            Ty::UInt => as_is!(),
            Ty::UBigInt => as_is!(),
            Ty::Float => as_is!(),
            Ty::Double => as_is!(),
            Ty::Timestamp => Self::from_timestamp(v.as_timestamp()),
            Ty::VarChar => Self::from_varchar(v.as_var_char()),
            Ty::NChar => Self::from_nchar(v.as_nchar()),
            Ty::Json => todo!(),
            _ => Self::null(),
        }
    }
}

impl<T> From<&T> for TaosBindV3
where
    T: IsValue,
{
    #[inline(always)]
    fn from(v: &T) -> Self {
        macro_rules! as_is {
            () => {
                Self::from_primitive(v)
            };
        }

        match T::TY {
            Ty::Null => Self::null(),
            Ty::Bool => as_is!(),
            Ty::TinyInt => as_is!(),
            Ty::SmallInt => as_is!(),
            Ty::Int => as_is!(),
            Ty::BigInt => as_is!(),
            Ty::UTinyInt => as_is!(),
            Ty::USmallInt => as_is!(),
            Ty::UInt => as_is!(),
            Ty::UBigInt => as_is!(),
            Ty::Float => as_is!(),
            Ty::Double => as_is!(),
            Ty::Timestamp => Self::from_timestamp(v.as_timestamp()),
            Ty::VarChar => Self::from_varchar(v.as_var_char()),
            Ty::NChar => Self::from_nchar(v.as_nchar()),
            Ty::Json => todo!(),
            _ => Self::null(),
        }
    }
}

pub trait ToBind: IsValue {
    fn to_bind(&self) -> TaosBind {
        macro_rules! as_is {
            () => {
                TaosBind::from_primitive(self)
            };
        }

        if self.is_null() {
            return TaosBind::null();
        }

        match Self::TY {
            Ty::Null => TaosBind::null(),
            Ty::Bool => as_is!(),
            Ty::TinyInt => as_is!(),
            Ty::SmallInt => as_is!(),
            Ty::Int => as_is!(),
            Ty::BigInt => as_is!(),
            Ty::UTinyInt => as_is!(),
            Ty::USmallInt => as_is!(),
            Ty::UInt => as_is!(),
            Ty::UBigInt => as_is!(),
            Ty::Float => as_is!(),
            Ty::Double => as_is!(),
            Ty::Timestamp => TaosBind::from_timestamp(self.as_timestamp()),
            Ty::VarChar => TaosBind::from_varchar(self.as_var_char()),
            Ty::NChar => TaosBind::from_nchar(self.as_nchar()),
            Ty::Json => todo!(),
            _ => TaosBind::null(),
        }
    }
}

impl<T: IsValue> ToBind for T {}

